import React, { useEffect, useState } from 'react';
import { connectWallet, getContract } from './utils/contract';
import { ethers } from 'ethers';

const App = () => {
  const [connected, setConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState('');
  const [prizePool, setPrizePool] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');
  const [ticketCount, setTicketCount] = useState(1);
  const [isRoundActive, setIsRoundActive] = useState(true);
  const [networkName, setNetworkName] = useState('');

  const handleConnectWallet = async () => {
    try {
      const address = await connectWallet();
      setWalletAddress(address);
      setConnected(true);
      setErrorMessage('');
      setNetworkName('Somnia Testnet');
    } catch (error) {
      setErrorMessage(`Error: ${error.message}`);
      console.error('Error connecting wallet:', error);
    }
  };

  const fetchData = async () => {
    try {
      const contract = getContract();
      const pool = await contract.prizePool();
      setPrizePool(ethers.formatEther(pool));

      // Get round status
      const roundStatus = await contract.isRoundActive();
      setIsRoundActive(roundStatus);

      try {
        // Get participants (this might fail if there's no length method)
        const participantsList = [];
        let i = 0;
        let hasMore = true;

        while (hasMore) {
          try {
            const participant = await contract.participants(i);
            participantsList.push(participant);
            i++;
          } catch (e) {
            hasMore = false;
          }
        }

        setParticipants(participantsList);
      } catch (err) {
        console.error("Error fetching participants:", err);
      }

      setLoading(false);
    } catch (error) {
      setErrorMessage('Error fetching data. Check console for details.');
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleBuyTickets = async () => {
    if (!connected) {
      setErrorMessage('Please connect your wallet first');
      return;
    }

    if (!isRoundActive) {
      setErrorMessage('Current lottery round is not active');
      return;
    }

    try {
      setLoading(true);
      const contract = getContract();
      const ticketPrice = ethers.parseEther('0.25'); // 0.25 STT per ticket
      const totalCost = ticketPrice * ethers.getBigInt(ticketCount);

      const tx = await contract.buyTickets(ticketCount, { value: totalCost });
      await tx.wait();

      setErrorMessage('');
      fetchData(); // Refresh data
    } catch (error) {
      setErrorMessage(`Error buying tickets: ${error.message}`);
      console.error('Error buying tickets:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    if (connected) {
      fetchData();
    }
  }, [connected]);

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">Lottery DApp</a>
          <div className="d-flex">
            {connected && <span className="navbar-text me-3">{networkName}</span>}
            <button
              className="btn custom-btn"
              onClick={handleConnectWallet}
              disabled={connected}
            >
              {connected ? `Connected: ${walletAddress.slice(0, 6)}...` : 'Connect Wallet'}
            </button>
          </div>
        </div>
      </nav>

      <div className="container mt-5">
        <h1 className="text-center text-primary">Welcome to the Lottery DApp</h1>
        {errorMessage && <div className="alert alert-danger text-center">{errorMessage}</div>}

        {loading ? (
          <div className="text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p>Loading...</p>
          </div>
        ) : (
          <>
            <div className="card mb-4">
              <div className="card-header">
                <h3 className="text-center mb-0">Current Prize Pool: {prizePool} STT</h3>
              </div>
              <div className="card-body">
                <p className="text-center">
                  Round Status: <span className={isRoundActive ? "text-success" : "text-danger"}>
                    {isRoundActive ? "Active" : "Waiting for winner selection"}
                  </span>
                </p>

                {isRoundActive && (
                  <div className="row justify-content-center">
                    <div className="col-md-6">
                      <div className="input-group mb-3">
                        <span className="input-group-text">Number of Tickets</span>
                        <input 
                          type="number" 
                          className="form-control" 
                          value={ticketCount} 
                          onChange={(e) => setTicketCount(Math.max(1, parseInt(e.target.value) || 1))}
                          min="1"
                        />
                        <button 
                          className="btn btn-primary" 
                          onClick={handleBuyTickets}
                          disabled={!connected}
                        >
                          Buy Tickets ({(0.25 * ticketCount).toFixed(2)} STT)
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            <div className="card">
              <div className="card-header">
                <h4 className="text-center mb-0">Participants ({participants.length})</h4>
              </div>
              <div className="card-body">
                {participants.length === 0 ? (
                  <p className="text-center">No participants yet. Be the first to buy a ticket!</p>
                ) : (
                  <ul className="list-group">
                    {participants.map((participant, index) => (
                      <li key={index} className="list-group-item">
                        {participant}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </>
  );
};

export default App;
